        uint256 _coll,
        uint256 _debt,
        uint256 _price
    ) external pure returns (uint) {
        return LiquityMath._computeCR(_coll, _debt, _price);
    }

    function calculateInterestOwed(
        uint256 _principal,
        uint16 _interestRate,
        uint256 startTime,
        uint256 endTime
    ) external pure returns (uint256) {
        return
            InterestRateMath.calculateInterestOwed(
                _principal,
                _interestRate,
                startTime,
                endTime
            );
    }
}

